//数据更新
function calculatePercent(data) {
    var total = data.reduce((sum, item) => sum + item.value, 0);
    return function (params) {
        var percent = ((params.value / total) * 100).toFixed(0);
        return params.name !== '' ? percent + '%' : '';
    };
}
function updatenongzuowu(data1, data2) {
    // 获取当前图表的配置选项
    var option = nongzuowu.getOption();
    // 更新图例数据
    option.legend[0].data = data1;
    // 更新饼图的数据项
    option.series[0].data = data2;
    option.series[0].label.formatter = calculatePercent(data2);
    // 应用更新后的配置选项到图表中
    nongzuowu.setOption(option);
}
function updateshuiguo(data1, data2) {
    // 获取当前图表的配置选项
    var option = shuiguo.getOption();
    // 更新图例数据
    option.legend[0].data = data1;
    // 更新饼图的数据项
    option.series[0].data = data2;
    option.series[0].label.formatter = calculatePercent(data2);
    // 应用更新后的配置选项到图表中
    shuiguo.setOption(option);
}
function updatehuafei(data2) {
    var option = huafei.getOption();
    option.series[0].data = data2;
    huafei.setOption(option);
}
function updatezaihai1(data1, data2) {
    var option = zaihai1.getOption();
    option.xAxis[0].data = ['水灾', '旱灾', '风雹', '冷冻'];
    option.series[0].data = data1;
    option.series[1].data = data2;
    zaihai1.setOption(option);
}

function updatezongchanzhi(data) {
    var option = zongchanzhi.getOption();
    option.yAxis[0].data = ['2022', '2021', '2020', '2019', '2018'];
    option.series[0].data = data;
    var maxData = Math.max.apply(null, option.series[0].data);
    option.series[1].data = option.series[1].data.map(function (value) {
        return 1.4 * maxData;
    });
    option.series[2].data = option.series[2].data.map(function (value) {
        return 1.41 * maxData;
    });
    zongchanzhi.setOption(option);
}
function updatenongyao(data) {
    var option = nongyao.getOption();
    option.xAxis[0].data = ['2018', '2019', '2020', '2021'];
    option.series[0].data = data;
    nongyao.setOption(option);
}
function updatezaihai(data1, data2) {
    var option = zaihai.getOption();
    option.xAxis[0].data = ['2018', '2019', '2020', '2021', '2022'];
    option.series[0].data = data1;
    option.series[1].data = data2;
    zaihai.setOption(option);
}

